 DROP PRIMARY KEY CASCADE;
DROP TABLE CO_INFORMATION_PAGE CASCADE CONSTRAINTS;

CREATE TABLE CO_INFORMATION_PAGE
(
 CO_INFORMATION_PAGE_ID NUMBER(*,0) NOT NULL ENABLE,
 CO_INFORMATION_CATEGORY VARCHAR2(50 BYTE) NOT NULL,	-- NURSING, ROSTER, ADMIN
 CO_INFORMATION_TYPE VARCHAR2(20 BYTE) NOT NULL,	-- PROGRAM, DOCUMENT
 CO_DESCRIPTION VARCHAR2(200 BYTE) NULL,
 CO_FUNCTION_ID VARCHAR2(50 BYTE) NULL,
 CO_FUNCTION_URL VARCHAR2(100 BYTE) NULL,
 CO_INFORMATION_SUB_CATEGORY VARCHAR2(50 BYTE) NULL,
 CO_DOCUMENT_ID NUMBER(*,0) NULL,
 CO_DOCUMENT_URL VARCHAR2(200 BYTE) NULL,
 CO_DOCUMENT_WITH_FOLDER VARCHAR2(1 BYTE) DEFAULT 'N',
 CO_DOCUMENT_LATEST_FILE VARCHAR2(1 BYTE) DEFAULT 'Y',
 CO_DOCUMENT_SHOW_SUB_FOLDER VARCHAR2(1 BYTE) DEFAULT 'Y',
 CO_DOCUMENT_ONLY_CURRENT_YEAR VARCHAR2(1 BYTE) DEFAULT 'N',
 CO_DOCUMENT_SHOW_POINT_FORM VARCHAR2(1 BYTE) DEFAULT 'N',
 CO_DOCUMENT_SHOW_ORDER VARCHAR2(1 BYTE) DEFAULT 'A', -- A: ascending, D: decending
 CO_FOLDER_SHOW_ROOT VARCHAR2(1 BYTE) DEFAULT 'Y', -- Y: show root folder
 CO_TARGET_CONTENT VARCHAR2(10 BYTE) DEFAULT 'content',
 CO_INTRANET_ONLY VARCHAR2(1 BYTE) DEFAULT 'N',
 CO_ALLOW_ADMIN VARCHAR2(1 BYTE) DEFAULT 'Y',
 CO_EXPAND_FOLDER VARCHAR2(1 BYTE) DEFAULT 'N',
 CO_CREATED_DATE DATE DEFAULT SYSDATE,
 CO_CREATED_USER VARCHAR2(30 BYTE) DEFAULT 'SYSTEM',
 CO_MODIFIED_DATE DATE DEFAULT SYSDATE,
 CO_MODIFIED_USER VARCHAR2(30 BYTE) DEFAULT 'SYSTEM',
 CO_ENABLED NUMBER(*,0) DEFAULT 1,
 PRIMARY KEY (CO_INFORMATION_PAGE_ID, CO_INFORMATION_CATEGORY)
);
