ALTER TABLE CRM_EXPORT_COLUMN
 DROP PRIMARY KEY CASCADE;
DROP TABLE CRM_EXPORT_COLUMN CASCADE CONSTRAINTS;

CREATE TABLE CRM_EXPORT_COLUMN
(
 CRM_EXPORT_COLUMN_ID VARCHAR2(50 BYTE) NOT NULL,
 CRM_EXPORT_COLUMN_LABEL VARCHAR2(50 BYTE) NOT NULL,
 CRM_CREATED_DATE DATE DEFAULT SYSDATE,
 CRM_CREATED_USER VARCHAR2(30 BYTE) DEFAULT 'SYSTEM',
 CRM_MODIFIED_DATE DATE DEFAULT SYSDATE,
 CRM_MODIFIED_USER VARCHAR2(30 BYTE) DEFAULT 'SYSTEM',
 CRM_ENABLED NUMBER(*,0) DEFAULT 1,
 PRIMARY KEY (CRM_EXPORT_COLUMN_ID)
);
