ALTER TABLE CRM_PARAMETER
 DROP PRIMARY KEY CASCADE;
DROP TABLE CRM_PARAMETER CASCADE CONSTRAINTS;

CREATE TABLE CRM_PARAMETER
(
 CRM_PARAMETER_TYPE VARCHAR2(20 BYTE) NOT NULL,
 CRM_PARAMETER_ID NUMBER(*,0) DEFAULT 1,
 CRM_PARAMETER_DESC VARCHAR2(100 BYTE) NULL,
 CRM_PARAMETER_LABEL VARCHAR2(20 BYTE) NULL,
 CRM_PARAMETER_PARENT_ID NUMBER(*,0) NULL,
 CRM_CREATED_DATE DATE DEFAULT SYSDATE,
 CRM_CREATED_USER VARCHAR2(30 BYTE) DEFAULT 'SYSTEM',
 CRM_MODIFIED_DATE DATE DEFAULT SYSDATE,
 CRM_MODIFIED_USER VARCHAR2(30 BYTE) DEFAULT 'SYSTEM',
 CRM_ENABLED NUMBER(*,0) DEFAULT 1,
 PRIMARY KEY (CRM_PARAMETER_TYPE, CRM_PARAMETER_ID)
);

















